var nexmo = require('../../nexmo')({key: '7a31fbcb', secret: '205a71d6'});
MockmAppMessager = function() {};
MockmAppMessager.prototype.message = function(req, callback) {
	var message = nexmo.sms({
		to: req.query.MessageNumber, 
		from: 'Mockm', 
		text: req.query.MessageText
	});
	message.send(function(err, results) {
        if (err) {
            callback(err, '');
        } else {
            callback(null, results);
        }
	});
}
module.exports = MockmAppMessager;